when rch_wpd_child_congential_deformity_rel.congential_deformity = ''1070'' then 1 else 0 end) as has_retinopathy_of_prematurity,\nmax(case when rch_wpd_child_master.other_congential_deformity is not null then 1 else 0 end) as has_any_other_birth_defect\nfrom  rch_wpd_child_master\ninner join rch_wpd_mother_master on rch_wpd_mother_master.id = rch_wpd_child_master.wpd_mother_id and rch_wpd_child_master.pregnancy_outcome = ''SBIRTH''\nand (rch_wpd_mother_master.state is null or rch_wpd_mother_master.state != ''MARK_AS_FALSE_DELIVERY'')\ninner join rch_pregnancy_analytics_details on rch_pregnancy_analytics_details.pregnancy_reg_id = rch_wpd_mother_master.pregnancy_reg_det_id\nleft join rch_wpd_child_congential_deformity_rel on rch_wpd_child_congential_deformity_rel.wpd_id = rch_wpd_child_master.id\ngroup by rch_wpd_child_master.id,rch_wpd_child_master.location_id,rch_wpd_child_master.date_of_delivery,rch_wpd_child_master.mother_id) as t;\n\n/*delete data before insert*/\ndelete from rch_child_analytics_details where member_id is null and wpd_child_id is not null;\n\ninsert into rch_child_analytics_details(wpd_child_id)\nselect rch_child_analytics_details_t.wpd_child_id from rch_child_analytics_details_t \nleft join rch_child_analytics_details \non rch_child_analytics_details_t.wpd_child_id  = rch_child_analytics_details.wpd_child_id\nwhere rch_child_analytics_details.wpd_child_id is null and rch_child_analytics_details_t.wpd_child_id is not null;\n\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED3''\nwhere event_config_id = 41 and status = ''PROCESSED2'';\ncommit;\nbegin;\nCREATE INDEX \n   ON rch_child_analytics_details_t (member_id DESC NULLS LAST);\n   CREATE INDEX \n   ON rch_child_analytics_details_t (wpd_child_id DESC NULLS LAST);\n   \n   /*\n   CREATE INDEX \n   ON rch_child_analytics_details (member_id DESC NULLS LAST);\n   CREATE INDEX \n   ON rch_child_analytics_details (wpd_child_id DESC NULLS LAST);\n   */\n   \nCREATE INDEX \n   ON rch_child_analytics_details_t (native_loc_id ASC NULLS LAST, dob DESC NULLS FIRST);\nCREATE INDEX \n   ON rch_child_analytics_details_t (delivery_location_id ASC NULLS LAST, dob DESC NULLS FIRST);\n\n-- update imt_member\n-- set additional_info = cast(additional_info as jsonb) || cast((''{\"highRiskReasons\":'' || (case when t.high_risk_reasons is not null then t.high_risk_reasons else ''null'' end) || ''}'') as jsonb)\n-- from\n-- (select distinct on (member_id)  t1.member_id, t1.high_risk_reasons\n-- from rch_child_analytics_details t\n-- inner join  rch_child_analytics_details_t t1\n-- on t1.member_id = t.member_id and t1.high_risk_reasons != t.high_risk_reasons\n-- order by member_id )as  t\n-- where imt_member.id = t.member_id;\n\nupdate rch_child_analytics_details set\n\tmember_state = t.member_state,\n\tmember_id = t.member_id,\n\tfamily_id = t.family_id,\n\tfirst_name = t.first_name,\n\tmiddle_name = t.middle_name,\n\tlast_name = t.last_name,\n\tmember_name = t.member_name,\n\tgender = t.gender,\n\twpd_child_id = t.wpd_child_id,\n\tnative_loc_id = t.native_loc_id,\n\tdelivery_location_id = t.delivery_location_id,\n\ttracking_location_id = t.tracking_location_id,\n\tis_valid_for_tracking_report = t.is_valid_for_tracking_report,\n\tloc_id = t.loc_id,\n\tdob = t.dob,\n\tdob_month_year = t.dob_month_year,\n\tfinancial_year = t.financial_year,\n\tdeath_date = t.death_date,\n\tplace_of_death = t.place_of_death,\n\tdeath_location_id = t.death_location_id,\n\tno_of_days_in_death = t.no_of_days_in_death,\n\tdeath_health_infra_id =  t.death_health_infra_id,\n\tbirth_weight = t.birth_weight,\n\tmother_id = t.mother_id,\n\tvitamin_k = t.vitamin_k,\n\tvitamin_k_loc = t.vitamin_k_loc,\n\tvitamin_k_health_infra = t.vitamin_k_health_infra,\n\tbcg = t.bcg,\n\tbcg_loc = t.bcg_loc,\n\tdpt1 = t.dpt1,\n\tdpt1_loc = t.dpt1_loc,\n\tpenta1 = t.penta1,\n\tpenta1_loc = t.penta1_loc,\n\topv0 = t.opv0,\n\topv0_loc = t.opv0_loc,\n\topv1 = t.opv1,\n\topv1_loc = t.opv1_loc,\n\the