rota_virus_1 is not null and rota_virus_1 >= ''07-01-2019'' then 1 else 0 end ) as rota_virus_1_given_0_1,\nsum(case when rota_virus_2 is not null and rota_virus_2 >= ''07-01-2019'' then 1 else 0 end ) as rota_virus_2_given_0_1,\nsum(case when rota_virus_3 is not null and rota_virus_3 >= ''07-01-2019'' then 1 else 0 end ) as rota_virus_3_given_0_1,\n\nsum(case when fully_immunization_in_number_of_month is not null and fully_immunization_in_number_of_month <=12 then 1 else 0 end) as fully_immunized_0_1,\n\nsum(case when has_done_breast_feeding_within_one_hour = true then 1 else 0 end) as breast_feeding_within_1_hr,\nsum(case when birth_weight is not null and birth_weight < 1.0 then 1 else 0 end ) as birth_weight_less_than_1,\nsum(case when birth_weight is not null and birth_weight >= 1.0 and birth_weight < 1.5 then 1 else 0 end ) as birth_weight_btw_1_and_1_5,\nsum(case when birth_weight is not null and birth_weight >= 1.5 and birth_weight < 2.0 then 1 else 0 end ) as birth_weight_btw_1_6_and_2,\nsum(case when birth_weight is not null and birth_weight >= 2.0 and birth_weight < 2.5 then 1 else 0 end ) as birth_weight_btw_2_1_and_2_5,\nsum(case when birth_weight is not null and birth_weight >= 2.5 and birth_weight < 3.0 then 1 else 0 end ) as birth_weight_btw_2_6_and_3,\nsum(case when birth_weight is not null and birth_weight >= 3.0 and birth_weight < 3.5 then 1 else 0 end ) as birth_weight_btw_3_1_and_3_5,\nsum(case when birth_weight is not null and birth_weight >= 3.5 and birth_weight < 4.0 then 1 else 0 end ) as birth_weight_btw_3_6_and_4,\nsum(case when birth_weight is not null and birth_weight >= 4.0 then 1 else 0 end ) as birth_weight_above_4,\nsum(case when member_id is not null and birth_weight is null then 1 else 0 end ) as birth_weight_not_reg\n\nfrom rch_child_analytics_details \nwhere is_valid_for_tracking_report = true\n--where member_id is not null\ngroup by tracking_location_id, financial_year;\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED7''\nwhere event_config_id = 41 and status = ''PROCESSED6'';\n\ncommit;\n\nbegin;\n\n--Child Health Services Provided during the year (Monthly)\n--Child Health Services Provided during the year (Yearly)\n--Child Health Services Provided during the year (Current Year)\ndrop table if exists child_services_given_analytics_t;\ncreate table child_services_given_analytics_t (\n\tid bigserial,\n\tlocation_id integer,\n\tmonth_year date,\n\tfinancial_year text,\n\tlive_births_since_april_1 integer,\n\tinfant_death integer,\n\tweighed integer,\n\tweighed_less_than_2_5 integer,\n\thep_b_given integer,\n\topv_0_given integer,\n\tvitamin_k_given integer,\n\tbcg_given integer,\n\tpenta_1_given integer,\n\topv_1_given integer,\n\tfipv_1_given integer,\n\tpenta_2_given integer,\n\topv_2_given integer,\n\tpenta_3_given integer,\n\topv_3_given integer,\n\tfipv_2_given integer,\n\tmeasles_1_given integer,\n\tmeasles_rubella_1_given integer,\n\tmeasles_rubella_2_given integer,\n\t\n\t\n\trota_virus_1_given integer,\n\trota_virus_2_given integer,\n\trota_virus_3_given integer,\n\t\n\tdpt_booster_given integer,\n\topv_booster_given integer,\n\tmeasles_or_rubella_2_given integer,\n\t\t\n\tfully_immunized integer,\n\tcomplete_immunized integer,\n\t\n\ttotal_delivery integer,\n\tlive_birth_delivery integer,\n\tstill_birth_delivery integer,\n\tneonatal_death integer,\n\tearly_neonatal_death integer,\n\tlbw_death integer,\n\tasphyxia_death integer,\n\tpnuemonia_death integer,\n\tdiarhoeaa_death integer,\n\tmeasles_death integer,\n\thigh_fever_death integer,\n\tother_death integer,\n\tprimary key(month_year, location_id)\n);\n\n--Child Health Services Provided during the year (Monthly)\n--Child Health Services Provided during the year (Yearly)\n--Child Health Services Provided during the year (Current Year)\nwith birth_det as(\n\tselect delivery_location_id as loc_id, dob_month_year as month_year, \n\tsum(case when member_id is not null then 1 else 0 end) as live_births_since_april_1,\n\tsum(case when member_id is not null and birth_weight is not null then 1 else 0 end) as weighed,\n\tsum(case when member_id is not null and birth_weight is not null and birth_weight < 2.5 then 1 else 0 end) as weighed_less_than_2_5,\n\tsum(case when day_of_delivery is not null then 1 else 0 end) as total_delivery,\n\tsum(case when day_of_delivery is not null and member_id is not null then 1 else 0 end) as live_birth_delivery,\n\tsum(case when day_of_delivery is not null and member_id is null then 1 else 0 end) as still_birth_delivery\n\tfrom rch_child_analytics_details \n\tgroup by delivery_location_id, dob_month_year\n), infant_death as(\n\tselect death_location_id as loc_id, cast(date_trunc(''month'', death_date) as date) as month_year,\n\tsum(case when no_of_days_in_death is not null and no_of_days_in_death between 0 and 365 then 1 else 0 end) as infant_death,\n\tsum(case when no_of_days_in_death is not null and no_of_days_in_death between 0 and 28 then 1 else 0 end) as neonatal_death,\n\tsum(case when no_of_days_in_death is not null and no_of_days_in_death between 0 and 7 then 1 else 0 end) as early_neonatal_death,\n\tsum(case when has_lbw_death = true then 1 else 0 end) as lbw_death,\n\tsum(case when has_asphyxia_death = true then 1 else 0 end) as asphyxia_death,\n\tsum(case when has_pnuemonia_death = true then 1 else 0 end) as pnuemonia_death,\n\tsum(case when has_sepsis_death = true then 1 else 0 end ) as has_sepsis_death,\n\tsum(case when has_diarhoeaa_death = true then 1 else 0 end) as diarhoeaa_death,\n\tsum(case when has_measles_death = true then 1 else 0 end) as measles_death,\n\tsum(case when has_high_fever_death = true then 1 else 0 end) as high_fever_death,\n\tsum(case when has_other_death = true then 1 else 0 end) as other_death\n\tfrom rch_child_analytics_details\n\tgroup by death_location_id, cast(date_trunc(''month'', death_date) as date)\n), hep_b as (\n\tselect hep_b_loc as loc_id, cast(date_trunc(''month'', hep_b) as date) as month_year,\n\tcount(*) as hep_b_given\n\tfrom rch_child_analytics_details\n\twhere hep_b is not null and hep_b_loc is not null\n\tgroup by hep_b_loc, cast(date_trunc(''month'', hep_b) as date)\n), opv0 as (\n\tselect opv0_loc as loc_id, cast(date_trunc(''month'', opv0) as date) as month_year,\n\tcount(*) as opv_0_given\n\tfrom rch_child_analytics_details\n\twhere opv0 is not null and opv0_loc is not null\n\tgroup by opv0_loc, cast(date_trunc(''month'', opv0) as date)\n), bcg as (\n\tselect bcg_loc as loc_id, cast(date_trunc(''month'', bcg) as date) as month_year,\n\tcount(*) as bcg_given\n\tfrom rch_child_analytics_details\n\twhere bcg is not null and bcg_loc is not null\n\tgroup by bcg_loc, cast(date_trunc(''month'', bcg) as date)\n), vitamin_k as (\n\tselect vitamin_k_loc as loc_id, cast(date_trunc(''month'', vitamin_k) as date) as month_year,\n\tcount(*) as vitamin_k_given\n\tfrom rch_child_analytics_details\n\twhere vitamin_k is not null and vitamin_k_loc is not null\n\tgroup by vitamin_k_loc, cast(date_trunc(''month'', vitamin_k) as date)\n), penta1 as (\n\tselect penta1_loc as loc_id, cast(date_trunc(''month'', penta1) as date) as month_year,\n\tcount(*) as penta_1_given\n\tfrom rch_child_analytics_details\n\twhere penta1 is not null and penta1_loc is not null\n\tgroup by penta1_loc, cast(date_trunc(''month'', penta1) as date)\n), opv1 as (\n\tselect opv1_loc as loc_id, cast(date_trunc(''month'', opv1) as date) as month_year,\n\tcount(*) as opv_1_given\n\tfrom rch_child_analytics_details\n\twhere opv1 is not null and opv1_loc is not null\n\tgroup by opv1_loc, cast(date_trunc(''month'', opv1) as date)\n), f_ipv1 as (\n\tselect f_ipv1_loc as loc_id, cast(date_trunc(''month'', f_ipv1) as date) as month_year,\n\tcount(*) as fipv_1_given\n\tfrom rch_child_analytics_details\n\twhere f_ipv1 is not null and f_ipv1_loc is not null\n\tgroup by f_ipv1_loc, cast(date_trunc(''month'', f_ipv1) as date)\n), penta2 as (\n\tselect penta2_loc as loc_id, cast(date_trunc(''month'', 