group by measles_loc, cast(date_trunc(''month'', measles) as date)\n), measles_rubbella_1 as (\n\tselect measles_rubella_loc as loc_id, cast(date_trunc(''month'', measles_rubella) as date) as month_year,\n\tcount(*) as measles_rubella_1\n\tfrom rch_child_analytics_details\n\twhere measles_rubella is not null and measles_rubella_loc is not null\n\tgroup by measles_rubella_loc, cast(date_trunc(''month'', measles_rubella) as date)\t\t\n), measles_rubbella_2 as (\n\tselect measles_rubella_2_loc as loc_id, cast(date_trunc(''month'', measles_rubella_2) as date) as month_year,\n\tcount(*) as measles_rubella_2\n\tfrom rch_child_analytics_details\n\twhere measles_rubella_2 is not null and measles_rubella_2_loc is not null\n\tgroup by measles_rubella_2_loc, cast(date_trunc(''month'', measles_rubella_2) as date)\t\t\n), rota_virus_1 as (\n\tselect rota_virus_1_loc as loc_id, cast(date_trunc(''month'', rota_virus_1) as date) as month_year,\n\tcount(*) as rota_virus_1_given\n\tfrom rch_child_analytics_details\n\twhere rota_virus_1 is not null and rota_virus_1 >= ''07-01-2019''\n\tgroup by rota_virus_1_loc, cast(date_trunc(''month'', rota_virus_1) as date)\t\t\n), rota_virus_2 as (\n\tselect rota_virus_2_loc as loc_id, cast(date_trunc(''month'', rota_virus_2) as date) as month_year,\n\tcount(*) as rota_virus_2_given\n\tfrom rch_child_analytics_details\n\twhere rota_virus_2 is not null and rota_virus_2 >= ''07-01-2019''\n\tgroup by rota_virus_2_loc, cast(date_trunc(''month'', rota_virus_2) as date)\t\t\n), rota_virus_3 as (\n\tselect rota_virus_3_loc as loc_id, cast(date_trunc(''month'', rota_virus_3) as date) as month_year,\n\tcount(*) as rota_virus_3_given\n\tfrom rch_child_analytics_details\n\twhere rota_virus_3 is not null and rota_virus_3 >= ''07-01-2019''\n\tgroup by rota_virus_3_loc, cast(date_trunc(''month'', rota_virus_3) as date)\t\t\n), fully_immunized as (\n\tselect native_loc_id as loc_id, cast(date_trunc(''month'', full_immunization_date) as date) as month_year,\n\tcount(*) as fully_immunized\n\tfrom rch_child_analytics_details\n\twhere full_immunization_date is not null and fully_immunization_in_number_of_month <= 12\n\tgroup by native_loc_id, cast(date_trunc(''month'', full_immunization_date) as date)\n), opv_booster as (\n\tselect opv_booster_loc as loc_id, cast(date_trunc(''month'', opv_booster) as date) as month_year,\n\tcount(*) as opv_booster_given\n\tfrom rch_child_analytics_details\n\twhere opv_booster is not null\n\tgroup by opv_booster_loc, cast(date_trunc(''month'', opv_booster) as date)\t\t\n), dpt_booster as (\n\tselect dpt_booster_loc as loc_id, cast(date_trunc(''month'', dpt_booster) as date) as month_year,\n\tcount(*) as dpt_booster_given\n\tfrom rch_child_analytics_details\n\twhere dpt_booster is not null \n\tgroup by dpt_booster_loc, cast(date_trunc(''month'', dpt_booster) as date)\t\t\n), measles_or_rubella_2 as (\n\tselect measles_or_rubella_2_loc as loc_id, cast(date_trunc(''month'', measles_or_rubella_2) as date) as month_year,\n\tcount(*) as measles_or_rubella_2_given\n\tfrom rch_child_analytics_details\n\twhere measles_or_rubella_2 is not null \n\tgroup by measles_or_rubella_2_loc, cast(date_trunc(''month'', measles_or_rubella_2) as date)\t\t\n), complete_immunized as (\n\tselect native_loc_id as loc_id, cast(date_trunc(''month'', complete_immunization_date) as date) as month_year,\n\tcount(*) as complete_immunized\n\tfrom rch_child_analytics_details\n\twhere complete_immunization_date is not null and complete_immunization_in_number_of_month <= 24\n\tgroup by native_loc_id, cast(date_trunc(''month'', complete_immunization_date) as date)\n)\n, month_year_loc_det as (\n\tselect loc_id, month_year from birth_det\n\tunion\n\tselect loc_id, month_year from infant_death\n\tunion\n\tselect loc_id, month_year from hep_b\n\tunion\n\tselect loc_id, month_year from opv0\n\tunion\n\tselect loc_id, month_year from bcg\n\tunion\n\tselect loc_id, month_year from vitamin_k\n\tunion\n\tselect loc_id, month_year from pen