birth_delivery,still_birth_delivery\n\t,neonatal_death,early_neonatal_death\n\t,lbw_death,asphyxia_death,pnuemonia_death,diarhoeaa_death\n\t,measles_death,high_fever_death,other_death\n\tfrom month_year_loc_det\n\tleft join birth_det on birth_det.loc_id = month_year_loc_det.loc_id and birth_det.month_year = month_year_loc_det.month_year\n\tleft join infant_death on infant_death.loc_id = month_year_loc_det.loc_id and infant_death.month_year = month_year_loc_det.month_year\n\tleft join hep_b on hep_b.loc_id = month_year_loc_det.loc_id and hep_b.month_year = month_year_loc_det.month_year\n\tleft join opv0 on opv0.loc_id = month_year_loc_det.loc_id and opv0.month_year = month_year_loc_det.month_year\n\tleft join vitamin_k on vitamin_k.loc_id = month_year_loc_det.loc_id and vitamin_k.month_year = month_year_loc_det.month_year\n\tleft join bcg on bcg.loc_id = month_year_loc_det.loc_id and bcg.month_year = month_year_loc_det.month_year\n\tleft join penta1 on penta1.loc_id = month_year_loc_det.loc_id and penta1.month_year = month_year_loc_det.month_year\n\tleft join opv1 on opv1.loc_id = month_year_loc_det.loc_id and opv1.month_year = month_year_loc_det.month_year\n\tleft join f_ipv1 on f_ipv1.loc_id = month_year_loc_det.loc_id and f_ipv1.month_year = month_year_loc_det.month_year\n\tleft join penta2 on penta2.loc_id = month_year_loc_det.loc_id and penta2.month_year = month_year_loc_det.month_year\n\tleft join opv2 on opv2.loc_id = month_year_loc_det.loc_id and opv2.month_year = month_year_loc_det.month_year\n\tleft join penta3 on penta3.loc_id = month_year_loc_det.loc_id and penta3.month_year = month_year_loc_det.month_year\n\tleft join opv3 on opv3.loc_id = month_year_loc_det.loc_id and opv3.month_year = month_year_loc_det.month_year\n\tleft join f_ipv2 on f_ipv2.loc_id = month_year_loc_det.loc_id and f_ipv2.month_year = month_year_loc_det.month_year\n\tleft join measles_or_rubella_1 on measles_or_rubella_1.loc_id = month_year_loc_det.loc_id and measles_or_rubella_1.month_year = month_year_loc_det.month_year\n\tleft join measles_1 on measles_1.loc_id = month_year_loc_det.loc_id and measles_1.month_year = month_year_loc_det.month_year\n\tleft join measles_rubbella_1 on measles_rubbella_1.loc_id = month_year_loc_det.loc_id and measles_rubbella_1.month_year = month_year_loc_det.month_year\n\tleft join measles_rubbella_2 on measles_rubbella_2.loc_id = month_year_loc_det.loc_id and measles_rubbella_2.month_year = month_year_loc_det.month_year\n\tleft join rota_virus_1 on rota_virus_1.loc_id = month_year_loc_det.loc_id and rota_virus_1.month_year = month_year_loc_det.month_year\n\tleft join rota_virus_2 on rota_virus_2.loc_id = month_year_loc_det.loc_id and rota_virus_2.month_year = month_year_loc_det.month_year\n\tleft join rota_virus_3 on rota_virus_3.loc_id = month_year_loc_det.loc_id and rota_virus_3.month_year = month_year_loc_det.month_year\n\tleft join fully_immunized on fully_immunized.loc_id = month_year_loc_det.loc_id and fully_immunized.month_year = month_year_loc_det.month_year\n\tleft join opv_booster on opv_booster.loc_id = month_year_loc_det.loc_id and opv_booster.month_year = month_year_loc_det.month_year\n\tleft join dpt_booster on dpt_booster.loc_id = month_year_loc_det.loc_id and dpt_booster.month_year = month_year_loc_det.month_year\n\tleft join measles_or_rubella_2 on measles_or_rubella_2.loc_id = month_year_loc_det.loc_id and measles_or_rubella_2.month_year = month_year_loc_det.month_year\n\tleft join complete_immunized on complete_immunized.loc_id = month_year_loc_det.loc_id and complete_immunized.month_year = month_year_loc_det.month_year\n\t\n)\ninsert into child_services_given_analytics_t(\n\tlocation_id, month_year, financial_year, live_births_since_april_1, infant_death,\n\tweighed, weighed_less_than_2_5, hep_b_given, opv_0_given, \n\tvitamin_k_given, \n\tbcg_given, penta_1_given, opv_1_given, fipv_1_given, penta_2_given, opv_2_given,\n\tpenta_3_given, opv_3_given, fipv_2_given, measles_1_given,measles_rubella_1_given,measles_rubella_2_given\n\t,rota_virus_1_given,rota_virus_2_given,rota_virus_3_given, fully_immunized\n\t,opv_booster_given,dpt_booster_given,measles_or_rubella_2_given,complete_immunized\n\t\n\t,total_delivery,live_birth_delivery,still_birth_delivery\n\t,neonatal_death,early_neonatal_death\n\t,lbw_death,asphyxia_death,pnuemonia_death,diarhoeaa_death\n\t,measles_death,high_fever_death,other_death\n\t\n)\nselect loc_id, month_year, financial_year, live_births_since_april_1, infant_death,\nweighed, weighed_less_than_2_5, hep_b_given, opv_0_given, \nvitamin_k_given, \nbcg_given, penta_1_given, opv_1_given, fipv_1_given, penta_2_given, opv_2_given, \npenta_3_given, opv_3_given, fipv_2_given,measles_1,measles_rubella_1,measles_rubella_2\n,rota_virus_1_given,rota_virus_2_given,rota_virus_3_given,fully_immunized\n,opv_booster_given,dpt_booster_given,measles_or_rubella_2_given,complete_immunized\n,total_delivery,live_birth_delivery,still_birth_delivery\n,neonatal_death,early_neonatal_death\n,lbw_death,asphyxia_death,pnuemonia_death,diarhoeaa_death\n,measles_death,high_fever_death,other_death\nfrom stat\nwhere month_year is not null and financial_year is not null;\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED8''\nwhere event_config_id = 41 and status = ''PROCESSED7'';\n\ncommit;\n\n\n-- add live child number entry\nbegin;\n    update rch_child_analytics_details set \n    live_birth_no = t.child_count\n    from (        \n        select a.id,count(1) as child_count \n\t\tfrom (select * from rch_child_analytics_details \n\t\t\twhere mother_id in (select distinct mother_id from rch_child_analytics_details_t)) a \n\t\tleft join rch_child_analytics_details b\n\t\t\ton a.mother_id = b.mother_id and b.member_state not in (''DEAD'')\n            where a.dob > b.dob\n\t\t\tand a.member_state not in (''DEAD'') \n\t\t\tgroup by a.id\n    ) as t where rch_child_analytics_details.id = t.id;   \n\ncommit;\n\n\n\nbegin;\n\n\n\ndrop table if exists rch_child_analytics_details_t;\t\n\n  \ndrop table if exists child_monthly_location_wise_analytics;\t\nALTER TABLE child_monthly_location_wise_analytics_t\n  RENAME TO child_monthly_location_wise_analytics;\n  \ndrop table if exists child_delivery_location_wise_monthly_analytics;\t\nALTER TABLE child_delivery_location_wise_monthly_analytics_t\n  RENAME TO child_delivery_location_wise_monthly_analytics;\n\ndrop table if exists child_yearly_location_wise_analytics;\t\nALTER TABLE child_yearly_location_wise_analytics_t\n  RENAME TO child_yearly_location_wise_analytics;\n\n\n\ndrop table if exists child_services_given_analytics;\t\nALTER TABLE child_services_given_analytics_t\n  RENAME TO child_services_given_analytics;\n\n  \n/*\nCREATE INDEX \n   ON rch_child_analytics_details (native_loc_id ASC NULLS LAST, full_immunization_date DESC NULLS LAST);\n   \nCREATE INDEX \n   ON rch_child_analytics_details (loc_id ASC NULLS LAST, dob DESC NULLS LAST); \nCREATE INDEX \n   ON rch_child_analytics_details (death_location_id ASC NULLS LAST, death_date DESC NULLS LAST); \n   \n */\n\n\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED9''\nwhere event_config_id = 41 and status = ''PROCESSED8'';\n\ncommit;\n\n\n\nbegin;\n/*\nwith rch_child_analytics_details_with_date as( \t\t\t\n\tSELECT\t\n\t\trca1.*\n\t\tfrom rch_child_analytics_details rca1\t\n\t\twhere rca1.dob > current_date - interval ''5 year''\n\t\tand rca1.member_id is not null\n\t\tand rca1.member_state != ''DEAD''\t\n\t),immunization_detail as (\n\tselect rca.member_id,\n\tconcat_ws('','',\n\tcase when rca.bcg is null and current_date >  rca.dob + cvs.bcg_from + interval ''1 month'' then ''BCG'' end,\n\tcase when rca.penta1 is null and current_date > rca.dob + cvs.penta_1_from + interval ''1 month'' then ''PENTA_1'' end,\n\tcase when rca.penta2 is null and current_date > rca.dob + cvs.penta_2_from + interval ''1 month'' then ''PENTA_2'' end,\n\tcase when rca.penta3 is null and current_date > rca.dob + cvs.penta_3_from + interval ''1 month'' then ''PENTA_3'' end,\n\tcase when rca.opv1 is null and current_date > rca.dob + cvs.opv_1_from + interval ''1 month'' then ''OPV_1'' end,\n\tcase when rca.opv2 is null and current_date > rca.dob + cvs.opv_2_from + interval ''1 month'' then ''OPV_2'' end,\n\tcase when rca.opv3 is null and current_date > rca.dob + cvs.opv_3_from + interval ''1 month'' then ''OPV_3'' end,\n\tcase when rca.measles_or_rubella_1 is null and  current_date > rca.dob + cvs.measles_rubella_1_from + interval ''1 month'' then ''MEASLES_RUBELLA_1'' end\n\t/*\n\tcase when rca.hep_b is null and current_date >  rca.dob + cvs.hepatitis_b_0_from + interval ''1 month'' then ''HEPATITIS B'' end,\n\tcase when rca.vitamin_k is null and current_date >  rca.dob + cvs.vitamin_k_from + interval ''1 month'' then ''VITAMIN K'' end,\n\t\n\tcase when rca.opv0 is null and current_date > rca.dob + cvs.opv_0_from + interval ''1 month'' then ''OPV 0'' end,\n\t\n\tcase when rca.rota_virus_1 is null and rca.dob >= ''06-01-2019'' and  current_date > rca.dob + cvs.rota_virus_1_from + interval ''1 month'' then ''ROTA VIRUS 1'' end,\n\tcase when rca.rota_virus_2 is null and rca.dob >= ''06-01-2019'' and current_date > rca.dob + cvs.rota_virus_2_from + interval ''1 month'' then ''ROTA VIRUS 2'' end,\n\tcase when rca.rota_virus_3 is null and rca.dob >= ''06-01-2019'' and current_date > rca.dob + cvs.rota_virus_3_from + interval ''1 month'' then ''ROTA VIRUS 3'' end,\n\t\n\tcase when rca.dpt1 is not null and current_date >  rca.dob + cvs.dpt_1_from + interval ''1 month'' then ''DPT 1'' end,\n\tcase when rca.dpt1 is not null and current_date >  rca.dob + cvs.dpt_2_from + interval ''1 month'' then ''DPT 2'' end,\n\tcase when rca.dpt1 is not null and current_date >  rca.dob + cvs.dpt_3_from + interval ''1 month'' then ''DPT 3'' end,\n\tcase when rca.f_ipv1 is null and  current_date > rca.dob + cvs.f_ipv_1_01_from + interval ''1 month'' then ''F IPV 1'' end,\n\tcase when rca.f_ipv2 is null and  current_date > rca.dob + cvs.f_ipv_2_01_from + interval ''1 month'' then ''F IPV 2'' end,\n\tcase when rca.opv_booster is null and  current_date > rca.dob + cvs.opv_booster_from + interval ''1 month'' then ''OPV BOOSTER'' end,\n\tcase when rca.dpt_booster is null and  current_date > rca.dob + cvs.dpt_booster_from + interval ''1 month'' then ''DPT BOOSTER'' end,\n\t\n\tcase when rca.measles_or_rubella_2 is null and  current_date > rca.dob + cvs.measles_rubella_2_from + interval ''1 month'' then ''MEASLES RUBELLA 2'' end*/\n\t)as vaccine\n\tfrom rch_child_analytics_details_with_date rca\n\tinner join child_vaccine_schedule cvs on true=true\n\t)\n\tupdate rch_child_analytics_details set overdue_immunisation = case when imm_det.vaccine != '''' then imm_det.vaccine else null end\n\tfrom immunization_detail imm_det where imm_det.member_id = rch_child_analytics_details.member_id;\n\t\n*/\nupdate system_configuration set key_value = to_char(current_date,''MM-DD-YYYY'') where system_key = ''rch_child_analytics_last_schedule_date'';\n\nupdate system_configuration set key_value = ''false'' where system_key = ''rch_child_analytics_run_for_all_child'';\n\n\n\n\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED10''\nwhere event_config_id = 41 and status = ''PROCESSED9'';\n\ncommit;\n\n/*\nbegin;\n\n\n--Child''s DOB base report for OPV/DPT booster and 2nd dose of Measles Service\ndrop table if exists child_monthly_analytics_16_24_months_t;\ncreate table child_monthly_analytics_16_24_months_t(\n\tid bigserial,\n\tlocation_id integer, \n\tmonth_year date,\n\tchildren_completed_age_16_24_months integer,\n\topv_booster_given_16_24_months integer,\n\tdpt_booster_given_16_24_months integer,\n\tmeasles_2_given_16_24_months integer,\n\tfully_immunized_16_24_months integer,\n\tnot_fully_immunized_16_24_months integer,\n\tinfant_death integer,\n\tprimary key(month_year, location_id)\n);\n\n\n\n--Child''s DOB base report for OPV/DPT booster and 2nd dose of Measles Service\ninsert into child_monthly_analytics_16_24_months_t(\n\tlocation_id, month_year,\n\tchildren_completed_age_16_24_months, \n\topv_booster_given_16_24_months, dpt_booster_given_16_24_months,\n\tmeasles_2_given_16_24_months, fully_immunized_16_24_months, not_fully_immunized_16_24_months, infant_death\n)\nselect native_loc_id, t.month_year,\ncount(*) as children_completed_age_16_24_months,\nsum(case when opv_booster is not null then 1 else 0 end) as opv_booster_given_16_24_months,\nsum(case when dpt_booster is not null then 1 else 0 end) as dpt_booster_given_16_24_months,\nsum(case when measles_2 is not null then 1 else 0 end) as measles_2_given_16_24_months,\nsum(case when fully_immunization_in_number_of_month is not null and opv_booster is not null\t\n\tand dpt_booster is not null and measles_2 is not null then 1 else 0 end) as fully_immunized_16_24_months,\nsum(case when fully_immunization_in_number_of_month is null or opv_booster is null\n\tor dpt_booster is null and measles_2 is null then 1 else 0 end) as not_fully_immunized_16_24_months,\nsum(case when is_infant_death is true then 1 else 0 end) as infant_death\nfrom rch_child_analytics_details\ninner join (\n\tselect cast(curr as date) as month_year, \n\tcast(curr - interval ''24 months'' as date) as from_date, cast(curr - interval ''16 months 1 day'' as date) as too_date\n\tfrom generate_series(date ''2013-01-01'', current_date, ''1 month'') as curr order by month_year desc\n) as t on rch_child_analytics_details.dob between t.from_date and t.too_date\nand member_id is not null\ngroup by native_loc_id, t.month_year;\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED10''\nwhere event_config_id = 41 and status = ''PROCESSED9'';\n\n\ncommit;\n*/\nbegin;\n\n/*\ndrop table if exists child_monthly_analytics_16_24_months;\t\nALTER TABLE child_monthly_analytics_16_24_months_t\n  RENAME TO child_monthly_analytics_16_24_months;\n\n*/\n\n\n  \n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED11''\nwhere event_config_id = 41 and status = ''PROCESSED10'';\n\ncommit;\n\nbegin;\n\n--Table Definition for Child Reports\n--Services Provided to the Children Registered during the Year (1 to 6 Year)\ndrop table if exists child_with_age_1_to_6_year_analytics_t;\ncreate table child_with_age_1_to_6_year_analytics_t (\n\tid bigserial,\n\tfinancial_year text,\n\tlocation_id integer,\n\ttotal_reg_1_6 integer,\n\tweighed_1_6 integer,\n\tweighed_less_than_2_5_1_6 integer,\n\tvitamin_k_given_1_6 integer,\n\tbcg_given_1_6 integer,\n\tdpt_1_given_1_6 integer,\n\tpenta_1_given_1_6 integer,\n\topv_1_given_1_6 integer,\n\tfipv_1_given_1_6 integer,\n\tdpt_2_given_1_6 integer,\n\tpenta_2_given_1_6 integer,\n\topv_2_given_1_6 integer,\n\tdpt_3_given_1_6 integer,\n\tpenta_3_given_1_6 integer,\n\topv_3_given_1_6 integer,\n\tfipv_2_given_1_6 integer,\n\tmeasles_1_given_1_6 integer,\n\tmeasles_rubella_1_given_1_6 integer,\n\tfully_immunized_1_6 integer,\n\tprimary key(financial_year, location_id)\n);\n\n\n--Queries to Insert into Analytics Table\n--Services Provided to the Children Registered during the Year ( 1 to 6 Year )\ninsert into child_with_age_1_to_6_year_analytics_t(\n\tfinancial_year, location_id,\n\ttotal_reg_1_6, weighed_1_6, weighed_less_than_2_5_1_6,vitamin_k_given_1_6, bcg_given_1_6, dpt_1_given_1_6, penta_1_given_1_6, opv_1_given_1_6,\n\tfipv_1_given_1_6, dpt_2_given_1_6, penta_2_given_1_6, opv_2_given_1_6, dpt_3_given_1_6, penta_3_given_1_6, opv_3_given_1_6,\n\tfipv_2_given_1_6, measles_1_given_1_6,measles_rubella_1_given_1_6, fully_immunized_1_6\n)\nselect t.financial_year, tracking_location_id,\ncount(*) as total_reg_1_6,\nsum(case when birth_weight is not null then 1 else 0 end ) as weighed_1_6,\nsum(case when birth_weight is not null and birth_weight < 2.5 then 1 else 0 end ) as weighed_less_than_2_5_1_6,\nsum(case when vitamin_k is not null then 1 else 0 end ) as vitamin_k_given_1_6,\nsum(case when bcg is not null then 1 else 0 end ) as bcg_given_1_6,\nsum(case when dpt1 is not null then 1 else 0 end ) as dpt_1_given_1_6,\nsum(case when penta1 is not null then 1 else 0 end ) as penta_1_given_1_6,\nsum(case when opv1 is not null then 1 else 0 end ) as opv_1_given_1_6,\nsum(case when f_ipv1 is not null then 1 else 0 end ) as fipv_1_given_1_6,\nsum(case when dpt2 is not null then 1 else 0 end ) as dpt_2_given_1_6,\nsum(case when penta2 is not null then 1 else 0 end ) as penta_2_given_1_6,\nsum(case when opv2 is not null then 1 else 0 end ) as opv_2_given_1_6,\nsum(case when dpt3 is not null then 1 else 0 end ) as dpt_3_given_1_6,\nsum(case when penta3 is not null then 1 else 0 end ) as penta_3_given_1_6,\nsum(case when opv3 is not null then 1 else 0 end ) as opv_3_given_1_6,\nsum(case when f_ipv2 is not null then 1 else 0 end ) as fipv_2_given_1_6,\nsum(case when measles is not null then 1 else 0 end ) as measles_1_given_1_6,\nsum(case when measles_rubella is not null then 1 else 0 end ) as measles_rubella_1_given_1_6,\nsum(case when fully_immunization_in_number_of_month is not null and fully_immunization_in_number_of_month <= 12 then 1 else 0 end) as fully_immunized_1_6\nfrom rch_child_analytics_details\ninner join (\n\tselect concat(extract(year from curr), ''-'', extract(year from curr) + 1) as financial_year, \n\tcast(curr - interval ''4 years'' as date) as from_date, to_date(''31-03-'' || extract(year from curr),''DD-MM-YYYY'') as too_date\n\tfrom generate_series(date ''2013-04-01'', current_date, ''1 year'') as curr order by financial_year desc\n) as t on rch_child_analytics_details.dob between t.from_date and t.too_date\nand member_id is not null and is_valid_for_tracking_report = true\ngroup by tracking_location_id, t.financial_year;\n\ndrop table if exists child_with_age_1_to_6_year_analytics;\t\nALTER TABLE child_with_age_1_to_6_year_analytics_t\n  RENAME TO child_with_age_1_to_6_year_analytics;\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''COMPLETED''\nwhere event_config_id = 41 and status = ''PROCESSED11'';\n\n\ncommit;\n\nbegin;\ndrop table if exists hmis_child_services_given_analytics_t;\ncreate table hmis_child_services_given_analytics_t (\n\thealth_infa_id integer,\n\tmonth_year date,\n\tlive_births_since_april_1 integer,\n\tinfant_death integer,\n\tweighed integer,\n\tweighed_less_than_2_5 integer,\n\thep_b_given integer,\n\topv_0_given integer,\n\tvitamin_k_given integer,\n\tbcg_given integer,\n\tpenta_1_given integer,\n\topv_1_given integer,\n\tfipv_1_given integer,\n\tpenta_2_given integer,\n\topv_2_given integer,\n\tpenta_3_given integer,\n\topv_3_given integer,\n\tfipv_2_given integer,\n\tmeasles_1_given integer,\n\tmeasles_rubella_1_given integer,\n\tmeasles_rubella_2_given integer,\n    measles_rubella_1_given_in_9_to_11_month integer,\n\tmeasles_rubella_2_given_in_9_to_11_month integer,\n\tbreast_feeding_within_1_hr integer,\n\trota_virus_1_given integer,\n\trota_virus_2_given integer,\n\trota_virus_3_given integer,\n\tdpt_booster_given integer,\n\topv_booster_given integer,\n\tmeasles_or_rubella_2_given integer,\n\tfully_immunized integer,\n    fully_immunized_bwtween_9_to_11_months integer,\n    fully_immunized_bwtween_9_to_11_months_male integer,\n    fully_immunized_bwtween_9_to_11_months_female integer,\n\tcomplete_immunized integer,\n\ttotal_delivery integer,\n\tlive_birth_delivery integer,\n\tstill_birth_delivery integer,\n\tneonatal_death integer,\n\tearly_neonatal_death integer,\n\tlbw_death integer,\n\tasphyxia_death integer,\n\tpnuemonia_death integer,\n\tdiarhoeaa_death integer,\n\tmeasles_death integer,\n\thigh_fever_death integer,\n\tother_death integer,\n    no_complications_observed integer,\n    no_of_complications_refered_to_hf integer,\n    no_of_deaths_in_1_to_24_hrs_at_facility integer,\n    no_of_deaths_in_1_to_24_hrs_at_community integer,\n    no_of_deaths_in_1_week_at_facility integer,\n    no_of_deaths_in_1_week_at_community integer,\n    no_of_deaths_in_8_to_28_days_at_facility integer,\n    no_of_deaths_in_8_to_28_days_at_community integer,\n    no_of_deaths_in_transit integer,\n    no_of_infant_deaths_1_to_12_months_at_facility integer,\n    no_of_infant_deaths_1_to_12_months_at_community integer,\n    no_of_infant_deaths_1_to_12_months_in_transit integer,\n    no_of_child_death_1_to_5_years_due_to_cause integer,\n    no_of_child_death_reported_through_cbcdr integer,\n    total_fbcdr_done integer,\n    no_of_cases_of_aefi_minor integer,\n    no_of_cases_of_aefi_severe integer,\n    no_of_cases_of_aefi_serious integer,\n    ifa_given integer,\n    vitamin_a_given integer,\n    unweighted_child_given_health_checkup integer,\n    childhood_diseases_pneumonia integer,\n    childhood_diseases_asthma integer,\n    childhood_diseases_sepsis integer,\n    childhood_diseases_diphtheria integer,\n    childhood_diseases_pertussis integer,\n    childhood_diseases_tetanus_neonatorum integer,\n    no_complications_managed_at_facility integer,\n    id_upto_4_weeks_dueto_sepsis  integer,\n    id_upto_4_weeks_dueto_asphyxia  integer,\n    id_upto_4_weeks_dueto_other  integer,\n    id_upto_12_moths_dueto_pneumonia integer,\n    id_upto_12_moths_dueto_diarrhoea integer,\n    id_upto_12_moths_dueto_feaver integer,\n    id_upto_12_moths_dueto_measles integer,\n    id_upto_12_moths_dueto_other integer,\n    cd_upto_5_years_dueto_pneumonia integer,\n    cd_upto_5_years_dueto_diarrhoea integer,\n    cd_upto_5_years_dueto_fever integer,\n    cd_upto_5_years_dueto_measles integer,\n    cd_upto_5_years_dueto_others integer,\n    sick_infant_provided_free_medicines_jssk integer,\n    sick_infant_provided_free_diagnostics_jssk integer,\n    sick_infant_provided_free_home_to_facility_trasport_jssk integer,\n    sick_infant_provided_free_home_interfacility_transfers_jssk integer,\n    sick_infant_provided_free_drop_back_to_home_jssk integer,\n    childhood_diseases_sam integer,\n\tprimary key(month_year, health_infa_id)\n);\ncommit;\n\nbegin;\nwith delivery_details as (\n    select\n    delivery_health_infrastructure as health_infa,\n    cast(date_trunc(''month'', dob_month_year) as date) as month_year,\n    sum(case when birth_weight is not null then 1 else 0 end ) birth_weight,\n    sum(case when birth_weight is not null and birth_weight < 2.5 then 1 else 0 end) as weighed_less_than_2_5,\n    sum(case when has_done_breast_feeding_within_one_hour then 1 else 0 end) as breast_feeding_within_1_hr\n    from rch_child_analytics_details\n    group by delivery_health_infrastructure,cast(date_trunc(''month'', dob_month_year) as date)\n), vitamin_k as (\n    select\n    vitamin_k_health_infra as health_infa,\n    cast(date_trunc(''month'', vitamin_k) as date) as  month_year,\n    sum(1) as vitamin_k_given\n    from rch_child_analytics_details\n    group by vitamin_k_health_infra, cast(date_trunc(''month'', vitamin_k) as date)\n), vitamin_a as (\n    select\n    vitamin_a_health_infra as health_infa,\n    cast(date_trunc(''month'', vitamin_a) as date) as  month_year,\n    sum(1) as vitamin_a_given\n    from rch_child_analytics_details\n    group by vitamin_a_health_infra, cast(date_trunc(''month'', vitamin_a) as date)\n), bcg as (\n    select\n    bcg_health_infra as health_infa,\n    cast(date_trunc(''month'', bcg) as date) as month_year,\n    sum(1) bcg_given\n    from rch_child_analytics_details\n    group by bcg_health_infra, cast(date_trunc(''month'', bcg) as date)\n), penta1 as (\n    select\n    pendta1_health_infra as health_infa,\n    cast(date_trunc(''month'', penta1) as date) as month_year,\n    sum(1) as penta_1_given\n    from rch_child_analytics_details\n    group by pendta1_health_infra, cast(date_trunc(''month'', penta1) as date)\n), penta2 as (\n    select\n    penta2_health_infra as health_infa,\n    cast(date_trunc(''month'', penta2) as date) as month_year,\n    sum(1) penta_2_given\n    from rch_child_analytics_details\n    group by penta2_health_infra, cast(date_trunc(''month'', penta2) as date)\n), penta3 as (\n    select\n    penta3_health_infra as health_infa,\n    cast(date_trunc(''month'', penta3) as date) as month_year,\n    sum(1) penta_3_given\n    from rch_child_analytics_details\n    group by penta3_health_infra, cast(date_trunc(''month'', penta3) as date)\n), opv0 as (\n    select\n    opv0_health_infra as health_infa,\n    cast(date_trunc(''month'', opv0) as date) as month_year,\n    sum(1) opv_0_given\n    from rch_child_analytics_details\n    group by opv0_health_infra, cast(date_trunc(''month'', opv0) as date)\n), opv1 as (\n    select\n    opv1_health_infra as health_infa,\n    cast(date_trunc(''month'', opv1) as date) as month_year,\n    sum(1) opv_1_given\n    from rch_child_analytics_details\n    group by opv1_health_infra, cast(date_trunc(''month'', opv1) as date)\n), opv2 as (\n    select\n    opv2_health_infra as health_infa,\n    cast(date_trunc(''month'', opv2) as date) as month_year,\n    sum(1) opv_2_given\n    from rch_child_analytics_details\n    group by opv2_health_infra, cast(date_trunc(''month'', opv2) as date)\n), opv3 as (\n    select\n    opv3_health_infra as health_infa,\n    cast(date_trunc(''month'', opv3) as date) as month_year,\n    sum(1) opv_3_given\n    from rch_child_analytics_details\n    group by opv3_health_infra, cast(date_trunc(''month'', opv3) as date)\n), hep_b as (\n    select\n    hep_b_health_infra as health_infa,\n    cast(date_trunc(''month'', hep_b) as date) as month_year,\n    sum(1) hep_b_given\n    from rch_child_analytics_details\n    group by hep_b_health_infra, cast(date_trunc(''month'', hep_b) as date)\n), fipv1 as (\n    select\n    f_ipv1_health_infra as health_infa,\n    cast(date_trunc(''month'', f_ipv1) as date) as month_year,\n    sum(1) fipv_1_given\n    from rch_child_analytics_details\n    group by f_ipv1_health_infra, cast(date_trunc(''month'', f_ipv1) as date)\n), fipv2 as (\n    select\n    f_ipv2_health_infra as health_infa,\n    cast(date_trunc(''month'', f_ipv2) as date) as month_year,\n    sum(1) fipv_2_given\n    from rch_child_analytics_details\n    group by f_ipv2_health_infra, cast(date_trunc(''month'', f_ipv2) as date)\n), rota1 as (\n    select\n    rota_virus_1_health_infra as health_infa,\n    cast(date_trunc(''month'', rota_virus_1) as date) as month_year,\n    sum(1) rota_virus_1_given\n    from rch_child_analytics_details\n    group by rota_virus_1_health_infra, cast(date_trunc(''month'', rota_virus_1) as date)\n), rota2 as (\n    select\n    rota_virus_2_loc_health_infra as health_infa,\n    cast(date_trunc(''month'', rota_virus_2) as date) as month_year,\n    sum(1) rota_virus_2_given\n    from rch_child_analytics_details\n    group by rota_virus_2_loc_health_infra, cast(date_trunc(''month'', rota_virus_2) as date)\n), rota3 as (\n    select\n    rota_virus_3_health_infra as health_infa,\n    cast(date_trunc(''month'', rota_virus_3) as date) as month_year,\n    sum(1) rota_virus_3_given\n    from rch_child_analytics_details\n    group by rota_virus_3_health_infra, cast(date_trunc(''month'', rota_virus_3) as date)\n), measles_or_rubella_1 as (\n    select\n    measles_or_rubella_1_health_infra as health_infa,\n    cast(date_trunc(''month'', measles_or_rubella_1) as date) as month_year,\n    sum(1) as measles_or_rubella_1_given \n    from rch_child_analytics_details\n    group by measles_or_rubella_1_health_infra, cast(date_trunc(''month'', measles_or_rubella_1) as date)\n),measles_and_rubella_1 as (\n    select\n    measles_rubella_health_infra as health_infa,\n    cast(date_trunc(''month'', measles_rubella) as date) as month_year,\n    sum(case when cast(measles_rubella as date) >= dob + interval ''9 months'' and cast(measles_rubella as date)  <= dob + interval ''11 months'' then 1 else 0 end) as measles_rubella_1_given_in_9_to_11_month\n    from rch_child_analytics_details\n    group by measles_rubella_health_infra, cast(date_trunc(''month'', measles_rubella) as date)\n), fully_immunized as (\n    select\n    delivery_health_infrastructure as health_infa,\n    cast(date_trunc(''month'', full_immunization_date) as date) as month_year,\n    sum(case when cast(full_immunization_date as date) >= dob + interval ''9 months'' and full_immunization_date  <= dob + interval ''12 months'' then 1 else 0 end) as fully_immunized_bwtween_9_to_11_months,\n    sum(case when gender= ''M'' and cast(full_immunization_date as date) >= dob + interval ''9 months'' and cast(full_immunization_date as date) <= dob + interval ''12 months'' then 1 else 0 end) as fully_immunized_bwtween_9_to_11_months_male,\n    sum(case when gender= ''F'' and cast(full_immunization_date as date) >= dob + interval ''9 months'' and cast(full_immunization_date as date) <= dob + interval ''12 months'' then 1 else 0 end) as fully_immunized_bwtween_9_to_11_months_female\n    from rch_child_analytics_details\n    group by delivery_health_infrastructure, cast(date_trunc(''month'', full_immunization_date) as date)\n), measles as (\n    select\n    measles_health_infra as health_infa,\n    cast(date_trunc(''month'', measles) as date) as month_year,\n    sum(1) as measles_1_given,\n    sum(case when cast(measles as date) >= dob + interval ''9 months'' and cast(measles as date)  <= dob + interval ''11 months'' then 1 else 0 end) as measles_rubella_2_given_in_9_to_11_month\n    from rch_child_analytics_details\n    group by measles_health_infra, cast(date_trunc(''month'', measles) as date)\n),measles_2 as (\n    select\n    measles_2_health_infra as health_infa,\n    cast(date_trunc(''month'', measles_2) as date) as month_year,\n    sum(case when cast(measles_2 as date) >= dob + interval ''16 months'' and cast(measles_2 as date)  <= dob + interval ''24 months'' then 1 else 0 end) as measles_2_given\n    from rch_child_analytics_details\n    group by measles_2_health_infra, cast(date_trunc(''month'', measles_2) as date)\n), measles_or_rubella_2 as (\n    select\n    measles_or_rubella_2_health_infra as health_infa,\n    cast(date_trunc(''month'', measles_or_rubella_2) as date) as month_year,\n    sum(1) as measles_or_rubella_2_given,\n    sum(case when cast(measles_or_rubella_2 as date) >= dob + interval ''16 months'' and cast(measles_or_rubella_2 as date) <= dob + interval ''24 months''  then 1 else 0 end) as measles_rubella_2_given\n    from rch_child_analytics_details\n    group by measles_or_rubella_2_health_infra, cast(date_trunc(''month'', measles_or_rubella_2) as date)\n),dpt1 as (\n    select\n    dpt1_health_infra as health_infa,\n    cast(date_trunc(''month'', dpt1) as date) as month_year,\n    sum(1) as dpt_booster_given\n    from rch_child_analytics_details\n    group by dpt1_health_infra, cast(date_trunc(''month'', dpt1) as date)\n), opv_booster as (\n    select\n    opv_booster_health_infra as health_infa,\n    cast(date_trunc(''month'', opv_booster) as date) as month_year,\n    sum(1) as opv_booster_given\n    from rch_child_analytics_details\n    group by opv_booster_health_infra, cast(date_trunc(''month'', opv_booster) as date)\n), diseases as (\n    select  \n    rch_child_service_master.health_infrastructure_id as health_infa, \n    lhcd.value as diseases_name,\n    cast(date_trunc(''month'', service_date) as date) as month_year,\n    sum(1) as value\n    from \n    rch_child_service_master\n    inner join rch_child_service_diseases_rel rcsd on rcsd.child_service_id = rch_child_service_master.id\n    inner join listvalue_field_value_detail lhcd on lhcd.id = rcsd.diseases \n    where lhcd.value in (''Pneumonia'',''Asthma'',''Sepsis'',''Diphtheria''\n    ,''Pertussis'',''Tetanus Neonatorum'', ''Severe Malnutrition''  )\n    group by rch_child_service_master.health_infrastructure_id, lhcd.value, cast(date_trunc(''month'', service_date) as date)\n), death_det as (\n    select \n    death_health_infra_id as health_infa,\n    cast(date_trunc(''month'', death_date) as date) as month_year,\n    sum(case when place_of_death = ''ON_THE_WAY'' then 1 else 0 end) as no_of_deaths_in_transit,\n    sum(c