(11,1027,'2018-08-08 10:31:14.569',1,'2021-01-13 20:22:06.427965',NULL,NULL,'TIMMER_BASED',NULL,NULL,0,15,'Update locationwise analytics table','[{"query":"select 1;","queryParam":null,"conditions":[{"isConditionReq":false,"condition":null,"conditionParam":null,"description":null,"notificaitonConfigsType":[{"id":"55b9b2aa-f0d3-4e6e-ab89-b30bca98dcd1","type":"QUERY","template":"update location_wise_analytics lwa \n                 set total_family = t.count \n                 from (select location_id,count(*) from imt_family group by location_id) t \n                 where t.location_id = lwa.loc_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null},{"id":"958157ab-822a-405e-abe1-7bb8065fd7fd","type":"QUERY","template":"update location_wise_analytics lwa \n                 set total_members = r.total_members, \n                 total_alive = r.total_alive,total_female = r.total_female,total_male = r.total_male,total_women = r.total_women, \n                  total_children = r.total_children,total_adolescents = r.total_adolescents \n                 from (select f.location_id ,count(*) as total_members, \n                 sum(case when m.state not in(''com.argusoft.imtecho.member.state.dead'',''com.argusoft.imtecho.member.state.dead.fhsr.verified'',''com.argusoft.imtecho.member.state.dead.fhsr.reverification'') then 1 else 0 end) as total_alive, \n                 sum(case when m.gender = ''F'' then 1 else 0 end) as total_female, \n                 sum(case when m.gender = ''M'' then 1 else 0 end) as total_male, \n                 sum(case when m.gender = ''F'' and extract(year from age(m.dob)) between 15 and 49 then 1 else 0 end) as total_women, \n                 sum(case when extract(year from age(m.dob)) between 0 and 5 then 1 else 0 end) as total_children, \n                 sum(case when extract(year from age(m.dob)) between 10 and 19 then 1 else 0 end) as total_adolescents \n                 from \n                 imt_member m inner join imt_family f on m.family_id = f.family_id \n                 group by f.location_id \n                 ) r \n                 where r.location_id = lwa.loc_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null},{"id":"a5ab6f22-cf36-4934-af13-aa55924c7757","type":"QUERY","template":"update location_wise_analytics lwa \n                 set sickle_male = t.\"Female\",sickle_female = t.\"Male\",sickle_0_5 = t.\"0-5\",sickle_5_15 = t.\"5-15\",\n                 sickle_15_45  = t.\"15-45\",sickle_above_45 = t.\"Above 45\",sickle_bpl = t.\"BPL\",\n                 sickle_apl  = t.\"APL\",sickle_sc = t.\"SC\",sickle_st = t.\"ST\",sickle_obc = t.\"OBC\",sickle_gen = t.\"GEN\", \n                 sickle_abandon = t.\"Abandon\",sickle_married = t.\"Married\",sickle_unmarried = t.\"Unmarried\",sickle_widow = t.\"Widow\",\n                 sickle_widower = t.\"Widower\" \n                 from \n                 ( \n                 select r1.location_id,\n                 sum(case when r1.gender = ''F'' then 1 else 0 end) as \"Female\",\n                 sum(case when r1.gender = ''M'' then 1 else 0 end) as \"Male\",\n                 sum(case when date_part(''year'',age(dob)) >= 0 and date_part(''year'',age(dob)) < 5 then 1 else 0 end) as \"0-5\",\n                 sum(case when date_part(''year'',age(dob)) >= 5 and date_part(''year'',age(dob)) < 15 then 1 else 0 end) as \"5-15\",\n                 sum(case when date_part(''year'',age(dob)) >= 15 and date_part(''year'',age(dob)) < 45 then 1 else 0 end) as \"15-45\",\n                 sum(case when date_part(''year'',age(dob)) >= 45  then 1 else 0 end) as \"Above 45\",\n                 sum(case when r1.bpl_flag = true then 1 else 0 end) as \"BPL\",\n                 sum(case when r1.bpl_flag = false then 1 else 0 end) as \"APL\",\n                 sum(case when r1.value = ''SC'' then 1 else 0 end) as \"SC\",\n                 sum(case when r1.value = ''ST'' then 1 else 0 end) as \"ST\",\n                 sum(case when r1.value = ''OBC/SEBC'' then 1 else 0 end) as \"OBC\",\n                 sum(case when r1.value = ''GENERAL'' then 1 else 0 end) as \"GEN\",\n                 sum(case when r1.mvalue = ''ABANDON'' then 1 else 0 end) as \"Abandon\",\n                 sum(case when r1.mvalue = ''MARRIED'' then 1 else 0 end) as \"Married\",\n                 sum(case when r1.mvalue = ''UNMARRIED'' then 1 else 0 end) as \"Unmarried\",\n                 sum(case when r1.mvalue = ''WIDOW'' then 1 else 0 end) as \"Widow\",\n                 sum(case when r1.mvalue = ''WIDOWER'' then 1 else 0 end) as \"Widower\" \n                 from \n                 (select * from \n                 imt_member mem inner join imt_family fam on mem.family_id = fam.family_id \n                 inner join imt_member_current_disease_rel cd on mem.id = cd.member_id \n                 inner join listvalue_field_value_detail lfvd on lfvd.id = cast(fam.caste as integer) \n                 inner join \n                 (select id,value as mvalue from listvalue_field_value_detail) r2 on r2.id = cast(mem.marital_status as integer)  \n                 where cd.current_disease_id = (select id from listvalue_field_value_detail where value = ''Sickle Cell Disease'') \n                 ) r1 \n                 group by (r1.location_id) \n                 ) t where t.location_id = lwa.loc_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null},{"id":"3b02c2d4-8022-49a4-8865-32fd4eaf5428","type":"QUERY","template":"update location_wise_analytics lwa \n                 set blindness = t.\"Blindness\",hiv = t.\"HIV\",sickle_cell = t.\"Sickle Cell Disease\",diabetes = t.\"Diabetes\",\n                 leprosy = t.\"Leprosy\",thalessemia = t.\"Thalessemia\",tb = t.\"TB\" from \n                 (select floc.location_id,\n                 sum (case when floc.chronic_disease_id = 724 then 1 else 0 end) as \"Blindness\",\n                 sum (case when floc.chronic_disease_id = 715 then 1 else 0 end) as \"TB\",\n                 sum (case when floc.chronic_disease_id = 729 then 1 else 0 end) as \"Sickle Cell Disease\",\n                 sum (case when floc.chronic_disease_id = 726 then 1 else 0 end) as \"Diabetes\",\n                 sum (case when floc.chronic_disease_id = 730 then 1 else 0 end) as \"Thalessemia\",\n                 sum (case when floc.chronic_disease_id = 735 then 1 else 0 end) as \"HIV\",\n                 sum (case when floc.chronic_disease_id = 716 then 1 else 0 end) as \"Leprosy\" \n                 from \n                 ((select * from imt_member_chronic_disease_rel cd union select * from imt_member_congenital_anomaly_rel cng union \n                 select * from imt_member_current_disease_rel cud union select * from imt_member_eye_issue_rel ed ) dmem \n                 left join imt_member mem \n                 on dmem.member_id = mem.id \n                 left join imt_family fam on fam.family_id = mem.family_id) floc  \n                 group by floc.location_id) t \n                 where t.location_id = lwa.loc_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null},{"id":"a28e5d43-b924-4200-8926-df4666b3358c","type":"QUERY","template":"update location_wise_analytics lwa \n                 set r11_15_49 = res.\"No of 15-49 yrs women in FHS\",r11_migrated = res.\"No of Women Migrated\",r11_dead = res.\"Death\",\n                 r11_eligible_couple = res.\"Eligible Couple\" from \n                 (select t.location_id, \n                 t.t1 as \"No of 15-49 yrs women in FHS\",t.t2 as \"No of Women Migrated\",\n                 t.t3 as \"Death\",t.t4 as \"Eligible Couple\" \n                 from \n                 (select fam.location_id, \n                 count(*) as t1,\n                 sum(case when mem.state = ''com.argusoft.imtecho.member.state.archived'' then 1 else 0 end) as t2,\n                 sum(case when mem.state = ''com.argusoft.imtecho.member.state.dead'' then 1 else 0 end) as t3,\n                 sum(case when lfvd.value = ''MARRIED'' then 1 else 0 end) as t4 \n                 from imt_member mem,imt_family fam,listvalue_field_value_detail lfvd \n                 where mem.family_id = fam.family_id  \n                 and mem.gender = ''F'' and extract(year from age(mem.dob)) between 15 and 49 \n                 and lfvd.id = cast(mem.marital_status as integer) \n                 group by rollup(fam.location_id)) t ) res \n                 where lwa.loc_id = res.location_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null},{"id":"eab8c1dc-d8f7-4300-a86a-957fcc5a5653","type":"QUERY","template":"update location_wise_analytics lwa \n                 set new_fam = res.new_fam,deleted_fam = res.deleted_fam,new_mem = res.new_mem,deleted_mem = res.deleted_mem \n                 from \n                 (select fam.location_id,\n                 sum(case when fam.state = ''com.argusoft.imtecho.family.state.new'' then 1 else 0 end) as \"new_fam\",\n                 sum(case when fam.state = ''com.argusoft.imtecho.family.state.archived'' then 1 else 0 end) as \"deleted_fam\",\n                 sum(case when mem.state = ''com.argusoft.imtecho.member.state.new'' then 1 else 0 end) as \"new_mem\",\n                 sum(case when mem.state = ''com.argusoft.imtecho.member.state.archived'' then 1 else 0 end) as \"deleted_mem\" \n                 from imt_member mem,imt_family fam \n                 where mem.family_id = fam.family_id   \n                 group by fam.location_id) res \n                 where lwa.loc_id = res.location_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null},{"id":"fdd1ebc0-1c71-47f5-91f1-d18ec4a00c5e","type":"QUERY","template":"update location_wise_analytics lwa \nset child_0_5 = res.t1,child_dead = res.t3,child_new = res.t4, \nchild_migrated = res.t2 from  \n(select case when fam.area_id is null then fam.location_id else fam.area_id end as location_id, \ncount(*) as t1,\nsum(case when mem.state = ''com.argusoft.imtecho.member.state.archived'' then 1 else 0 end) as t2,\nsum(case when mem.state = ''com.argusoft.imtecho.member.state.dead'' then 1 else 0 end) as t3,\nsum(case when mem.state = ''com.argusoft.imtecho.member.state.new'' then 1 else 0 end) as t4 \nfrom imt_member mem,imt_family fam \nwhere mem.family_id = fam.family_id \nand extract(year from age(mem.dob)) between 0 and 5  \ngroup by case when fam.area_id is null then fam.location_id else fam.area_id end) res \nwhere lwa.loc_id = res.location_id;","mobileNotificationType":null,"emailSubject":null,"emailSubjectParameter":null,"templateParameter":null,"baseDateFieldName":null,"refCode":null,"memberFieldName":null,"userFieldName":null,"familyFieldName":null,"trigerWhen":"IMMEDIATELY","day":null,"hour":null,"miniute":null,"mobileNotificationConfigs":null,"configId":11,"queryMasterId":null,"queryCode":null,"queryMasterParamJson":null,"exceptionType":null,"exceptionMessage":null,"smsConfigJson":null}],"uuid":null,"isFromOtherServer":null}],"uuid":null,"isFromOtherServer":null}]','ACTIVE','DAILY',NULL,'f8dd79d2-25fd-41a8-8d04-11cda16bebce'),
(64,409,'2019-05-06 19:46:49.698',1,'2021-01-13 20:22:06.427965',NULL,NULL,'TIMMER_BASED',NULL,NULL,6,0,'Eligible couple analyst','[{"query":"select\nkey_value as last_updated_at\nfrom system_configuration\nwhere system_key = ''LAST_UPDATE_DATE_OF_ELIGIBLE_COUPLE_ANALYTICS'';","queryParam":null,"conditions":[{"isConditionReq":false,"condition":null,"conditionParam":null,"description":null,"notificaitonConfigsType":[{"id":"f7ae9975-017a-4939-af18-c7d269fac45a","type":"QUERY","template":"begin;\n/*\ndrop table if exists rch_eligible_couple_analytics_t;\nCREATE TABLE rch_eligible_couple_analytics_t\n(\n  member_id integer NOT NULL,\n  dob date,\n  last_method_of_contraception character varying(15),\n  loc_id integer,\n  children_count int,\n  member_basic_state text,\n  PRIMARY KEY (member_id)\n);\n*/\n--insert data for rch_eligible_couple_analytics_t\nwith last_updated as (\nselect\nto_timestamp(key_value, ''YYYY-MM-DD HH24:MI:SS.US'') as last_updated_at\nfrom system_configuration\nwhere system_key = ''LAST_UPDATE_DATE_OF_ELIGIBLE_COUPLE_ANALYTICS''\n),member_data as(\nselect  m.id, m.dob, m.last_method_of_contraception\n, case when f.area_id is null then f.location_id else f.area_id end as loc_id\n, m.basic_state\nfrom imt_member m\ninner join imt_family f on m.family_id = f.family_id\nwhere\n  m.basic_state in (''VERIFIED'', ''NEW'',''REVERIFICATION'',''TEMPORARY'')\n  and f.basic_state in (''VERIFIED'', ''NEW'',''REVERIFICATION'',''TEMPORARY'')\n  and m.marital_status = 629\n  and m.gender = ''F''\n  and (m.dob < now() - interval ''15 years'' and m.dob > now() - interval ''49 years'')\n  and f.modified_on >= ''#last_updated_at#''\n),child_count as (\n    select sum(case when mother_id is not null then 1 else 0 end) as children_count ,mother_id \n    from member_data  \n    inner join imt_member m on member_data.id = m.mother_id\n   group by mother_id \n)\ninsert into rch_eligible_couple_analytics(member_id,dob,last_method_of_contraception,loc_id,children_count,member_basic_state)  \nselect id, dob, last_method_of_contraception,loc_id, children_count,basic_state\nfrom member_data\nleft join child_count on member_data.id = child_count.mother_id\non conflict (member_id)\nDO UPDATE SET dob = EXCLUDED.dob\n\t ,last_method_of_contraception = EXCLUDED.last_method_of_contraception\n\t ,loc_id = EXCLUDED.loc_id\n\t ,children_count = EXCLUDED.children_count\n\t ,member_basic_state = EXCLUDED.member_basic_state;\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED1''\nwhere event_config_id = 64 and status = ''PROCESSED'';\n\ncommit;\n\nbegin;\n/*Update query to change updated dob*/\nupdate rch_eligible_couple_analytics set dob = t1.dob\nfrom (\nselect m.id as member_id,m.dob\nfrom (select  rca1.member_id\nfrom rch_eligible_couple_analytics rca1\nleft join imt_member m on rca1.member_id = m.id and rca1.dob = cast(m.dob as date)\nwhere m.id is null) as t,imt_member m\nwhere t.member_id = m.id) as t1 \nwhere t1.member_id = rch_eligible_couple_analytics.member_id;\n\n/*Delete member or family archive*/\ndelete from rch_eligible_couple_analytics where member_id in (\nselect rca.member_id\nfrom rch_eligible_couple_analytics rca\nleft join imt_member m on rca.member_id = m.id and m.basic_state in (''VERIFIED'', ''NEW'',''REVERIFICATION'',''TEMPORARY'')\nleft join imt_family f on m.family_id = f.family_id and f.basic_state in (''VERIFIED'', ''NEW'',''REVERIFICATION'',''TEMPORARY'')\nwhere f.id is null\n);\n\n\n/*Delete query for femate to male member*/\ndelete from rch_eligible_couple_analytics where member_id in (\nselect rca.member_id\nfrom rch_eligible_couple_analytics rca\nleft join imt_member m on rca.member_id = m.id  and m.gender = ''F''\nwhere m.id is null\n);\n\n\n/*delete member whose age is not as per eligible couple*/\t \ndelete from rch_eligible_couple_analytics where dob >= now() - interval ''15 years'';\ndelete from rch_eligible_couple_analytics where dob <= now() - interval ''49 years'';\n\n\nupdate timer_event SET completed_on = clock_timestamp(),status = ''PROCESSED2''\nwhere event_config_id = 64 and status = ''PROCESSED1'';\n\ncommit;\nbegin;\n--rch_eligible_couple_location_wise_count_anlytics_detail\ndrop table if exists rch_eligible_couple_location_wise_count_anlytics_detail_t;\nCREATE TABLE rch_eligible_couple_location_wise_count_anlytics_detail_t\n(\n  location_id integer not null, \n  age_group_or_child_cnt char varying(30),\n  eligiblecouple integer,\n  male integer,\n  female integer, \n  coppert integer, \n  condom integer, \n  orelpills integer, \n  injectable integer, \n  none integer,\n  PRIMARY KEY (location_id,age_group_or_child_cnt)\n);\n\n--insert data for rch_eligible_couple_location_wise_count_anlytics_detail_t\ninsert into rch_eligible_couple_location_wise_count_anlytics_detail_t\nwith children_count_label as (\n    select ''0'' as children, 0 as child_count\n    union \n    select ''1'' as children, 1 as child_count\n    union\n    select ''2'' as children, 2 as child_count\n    union\n    select ''3'' as children, 3 as child_count\n    union\n    select ''4'' as children, 4 as child_count\n    union\n    select ''5+'' as children, 5 as child_count\n)\n,child_wise_eligible_couple as(\nselect m.loc_id,\ncoalesce(case when children_count > 4 then 5 else children_count end, 0) as child_cnt, count(*) as eligiblecouple,\ncoalesce(sum ( case when m.last_method_of_contraception = (''MLSTR'')  then 1 else 0 end),0) as male,\ncoalesce(sum ( case when m.last_method_of_contraception = (''FMLSTR'')  then 1 else 0 end ),0) as female,\ncoalesce(sum ( case when m.last_method_of_contraception in (''IUCD5'',''IUCD10'')  then 1 else 0 end),0)  as coppert,\ncoalesce(sum ( case when m.last_method_of_contraception = (''CONDOM'')  then 1 else 0 end ),0)  as condom,\ncoalesce(sum ( case when m.last_method_of_contraception = (''ORALPILLS'')  then 1 else 0 end ),0)  as orelpills,\ncoalesce(sum ( case when m.last_method_of_contraception in (''CHHAYA'',''ANTARA'')  then 1 else 0 end ),0)  as injectable,\ncoalesce(sum ( case when m.last_method_of_contraception is null or m.last_method_of_contraception in (''CONTRA'',''NONE'',''null'',''none'')  then 1 else 0 end ),0)  as none\nfrom rch_eligible_couple_analytics m\ngroup by m.loc_id,child_cnt\n)\nselect child.loc_id, c.children as age_group_or_child_cnt, child.eligiblecouple, child.male, child.female, \nchild.coppert, child.condom, child.orelpills, child.injectable, child.none\nfrom child_wise_eligible_couple child\ninner join children_count_label c on c.child_count = child.child_cnt\nunion all\n(with age_group as (\n    select ''15-19'' as age_group, current_date - interval ''19 years'' as from_date, current_date - interval ''15 years''  as to_date\n    union\n    select ''20-24'' as age_group,  current_date - interval ''24 years'' as from_date, current_date - interval ''19 years'' - interval ''1 day''  as to_date\n    union\n    select ''25-29'' as age_group, current_date - interval ''29 years'' as from_date, current_date - interval ''24 years'' - interval ''1 day''  as to_date\n    union\n    select ''30-34'' as age_group,  current_date - interval ''34 years'' as from_date, current_date - interval ''29 years'' - interval ''1 day''  as to_date\n    union\n    select ''35-39'' as age_group, current_date - interval ''39 years'' as from_date, current_date - interval ''34 years'' - interval ''1 day''  as to_date\n    union\n    select ''40-44'' as age_group,  current_date - interval ''44 years'' as from_date, current_date - interval ''39 years'' - interval ''1 day''  as to_date\n    union\n    select ''45-49'' as age_group, current_date - interval ''49 years'' as from_date, current_date - interval ''44 years'' - interval ''1 day''  as to_date\n)\nselect m.loc_id,age_group.age_group as filterpoint,count(*) as eligiblecouple,\nsum ( case when m.last_method_of_contraception = (''MLSTR'')  then 1 else 0 end) as male,\nsum ( case when m.last_method_of_contraception = (''FMLSTR'')  then 1