  ,phi_del_3_ancs,breast_feeding,maternal_detah,dh,mdh,delivery_108,delivery_out_of_state_govt,delivery_out_of_state_pvt
  from rch_delivery_date_base_location_wise_data_point,location_hierchy_closer_det
