	extract(month from age(cast(dates.to_date as date),cast(dates.from_date as date))) as no_of_in_period_months,
	coalesce(
	counts.less_than_3_sd
	+ counts.muac_less_than_11_5
	+ counts.sd_and_muac
	+ counts.oedema
	+ counts.height_less_than_45
	,0) as criteria_total,
