                case
                    when (
                        (
                            cast(rpa.lmp_date + interval ''180 days'' as date),
                            cast(rpa.lmp_date + interval ''270 days'' as date)
                        ) overlaps (
                            cast(dates.from_date as date),
                            cast(dates.to_date as date)
                        )
                    ) then rpa.lmp_date + interval ''270 days''
                    else null
