	imt_member.id in(
		select child_id
	from
		member_det)),
loc as (
select
	distinct loc_id
from
	member_det ),
loc_det as (
select
	loc.loc_id,
	string_agg((case when ( prefered_language.is_enlish and (l2.english_name is not null)) then l2.english_name else l2.name end),''>'' order by lhcd.depth desc) as aoi 
