from
	um_user_location ul,
	um_user u,
	loc
where
	loc.loc_id = ul.loc_id
	and u.id = ul.user_id
	and u.state = ''ACTIVE''
	and ul.state = ''ACTIVE''
	and u.role_id = (
	select
