	group by loc.loc_id
), fhw_det as (
	select loc.loc_id, 
	concat(u.first_name, '' '', u.last_name, '' ('', u.user_name, '')'', ''<br>'', ''Contact : '', case when u.contact_number is not null then u.contact_number else ''N/A'' end) as fhw
