	 (1346,69973,'2019-08-20 13:06:57.584',1,'2021-01-13 20:22:06.427965','from_date,to_date,location_id','with dates as(
select
to_date(case when ''#from_date#'' =''null'' then null else ''#from_date#'' end,''MM/DD/YYYY'') as from_date,
