	det.not_entered_count as "Facility Count Not Entered Data"
	from det
union all
select null,
''<b>Total<b>'',
#location_id#,
sum(det.total_institutions),
sum(det.not_entered_count)
from det',true,'ACTIVE','24c4a1e1-44f7-43ae-aded-f85d9d840fb1'),
