left join hwc_details hd on hd.parent_id = ld.child_id
left join daily_login_report dlr on dlr.parent_id = ld.child_id
left join location_master lm   
    on lm.id = ld.child_id
union all
select
	#location_id#,
	''<b>Total</b>'',
