dates AS (
    SELECT
        to_date(
            CASE WHEN ''#from_date#'' = ''null'' THEN
                NULL
            ELSE
                ''#from_date#''
            END, ''MM-DD-YYYY'') AS from_date,
        to_date(case when ''#to_date#'' = ''null'' then null else ''#to_date#'' end,''MM/DD/YYYY'') + interval ''1 month'' - interval ''1 millisecond'' as to_date
