,user_wise_count AS (
    SELECT
 		ul.user_id,
 		string_agg(distinct get_location_hierarchy(ul.location_id),''<br>'') as location_name,
 		1 as number_of_days,
 		max(case when is_logged_in = true then 1 else 0 end) as number_of_logged_in_days
