and extract(''ISODOW'' FROM ul.login_date) <= 6
        AND lh.parent_id = #location_id#
        and lh.child_id = ul.location_id
        AND role_id = #role_id#
        GROUP BY
          ul.login_date, ul.user_id
order by ul.login_date, ul.user_id
        
)

